mapper.insert:
		INSERT INTO classroom(
			classroom_number,
			classroom_size,
			classroom_total
		)VALUES (
			#{classroomNumber},
			#{classroomSize},
			#{classroomTotal}
		)

mapper.update:
		UPDATE classroom
		SET
			classroom_number = #{classroomNumber},
			classroom_size = #{classroomSize},
			classroom_total = #{classroomTotal}
		WHERE classroom_no =#{classroomNo}
