mapper.select:
SELECT lms_notice_no, lms_notice_title, lms_notice_writer, lms_notice_updatedate
FROM lms_notice
LIMIT #{beginRow}, #{rowPerPage}

SELECT lms_notice_no, lms_notice_title, lms_notice_content, lms_notice_writer, lms_notice_updatedate, lms_notice_count
FROM lms_notice
WHERE lms_notice_no = #{lmsNoticeNo}
