mapper.update:

UPDATE message_receive
SET is_confirm = "Y"
WHERE 
	message_no = #{messageNo} 
	AND to_id = #{id}


UPDATE message_send
SET is_confirm = "Y"
WHERE
	message_no = #{messageNo} 
	AND to_id = #{id}

mapper.select:
SELECT COUNT(message_no) 
FROM message_receive
WHERE to_id = #{accountId}

SELECT COUNT(message_no) 
FROM message_send
WHERE from_id = #{accountId}

SELECT COUNT(message_no) 
FROM message_send
WHERE 
	from_id = #{accountId} 
	AND to_id = #{search}
