mapper.update:
UPDATE teacher
teacher_email = #{teacherEmail}, teacher_name = #{teacherName}, teacher_phone = #{teacherPhone}, teacher_gender = #{teacherGender}, teacher_birth = #{teacherBirth}, teacher_address_main = #{teacherAddressMain}, teacher_address_sub = #{teacherAddressSub}, teacher_image = #{teacherImage}, teacher_info = #{teacherInfo}, teacher_updatedate = NOW()
WHERE account_id = #{accountId}

UPDATE teacher
teacher_image = #{teacherImage}, teacher_updatedate = NOW()
WHERE account_id = #{accountId}

mapper.select:
SELECT teacher_image
FROM teacher
WHERE  account_id = #{accountId}
